aot-command:
cd wabt/wasm2c && ../build/wasm2c {input} -o temp.c && name=$(basename {input} .wasm | sed 's/-/_/g') && echo '#include <stdio.h>
#include <stdlib.h>
#include "temp.h"
int main(int argc, char** argv) {{
  wasm_rt_init();
  w2c_'$name' instance;
  wasm2c_'$name'_instantiate(&instance);
  wasm2c_'$name'_free(&instance);
  wasm_rt_free();
  return 0;
}}' > main.c && gcc main.c temp.c wasm-rt-impl.c wasm-rt-mem-impl.c -Iwasm2c -lm -o {output} && rm temp.c temp.h main.c
